import 'react-big-calendar/lib/css/react-big-calendar.css';
import React, {useState, useEffect, useRef, useCallback } from 'react'
import { useParams } from 'react-router-dom'
import DateTimePicker from 'react-datetime-picker';
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'

const localizer = momentLocalizer(moment)


const WorkerPage = () => {
  const [appDate, setAppDate] = useState(new Date());
  let { worker_id } = useParams();
  let [worker, setWorker]= useState([]);
  let [appointmens, setAppointments] = useState([])

  useEffect(() => {
    getWorkers()
    getAppointments()
  }, [])

  let getWorkers = async () => {
    let response = await fetch(`http://127.0.0.1:8000/api/worker/${worker_id}/`)
    let data = await response.json()
    console.log("data: ", data)
    setWorker(data)
  }

  let getAppointments = async () => {
    let response = await fetch(`http://127.0.0.1:8000/api/worker/${worker_id}/appointments/`)
    let data = await response.json()
    console.log("Appointments: ", data)
  }

  let setAppointment = () => {
    fetch(`http://127.0.0.1:8000/api/add-appointment/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        start_time: appDate.getHours() + ':' + appDate.getMinutes(),
        end_time: (appDate.getHours() + 1) + ':' + appDate.getMinutes(),
        date: appDate.getFullYear() + '-' + appDate.getMonth() + '-' + appDate.getDate(),
        worker: worker_id
      })
    })
    console.log("Submit appointment")

  }

  console.log("Value: ", appDate.getFullYear())
  console.log(JSON.stringify({
    start_time: appDate.getHours() + ':' + appDate.getMinutes(),
    end_time: (appDate.getHours() + 1) + ':' + appDate.getMinutes(),
    date: appDate.getDate(),
    worker: worker_id
  }))

  let myEventsList = [
    {
      id: 4,
      title: "Some Event",
      start: new Date(2022, 6, 9, 0, 0, 0),
      end: new Date(2022, 6, 9, 1, 0, 0)
    },
  ]

  const clickRef = useRef(null)

  useEffect(() => {
    /**
     * What Is This?
     * This is to prevent a memory leak, in the off chance that you
     * teardown your interface prior to the timed method being called.
     */
    return () => {
      window.clearTimeout(clickRef?.current)
    }
  }, [])

  const onSelecting = useCallback((range) => {
    /**
     * Here we are waiting 250 milliseconds (use what you want) prior to firing
     * our method. Why? Because both 'click' and 'doubleClick'
     * would fire, in the event of a 'doubleClick'. By doing
     * this, the 'click' handler is overridden by the 'doubleClick'
     * action.
     */
    window.clearTimeout(clickRef?.current)
    clickRef.current = window.setTimeout(() => {
      console.log(range['start'])
      setAppDate(range['start'])
    }, 250)
  }, [])

  return (
    <div>
      <h3>{worker.first_name} {worker.last_name}</h3>
      <small>{worker.specialization}</small>
      <div>{worker.info}</div>
      <div>Worker Calendar:</div>
      <Calendar
        localizer={localizer}
        events={myEventsList}
        views={['month','work_week', 'day']}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        onSelectSlot={onSelecting}
        selectable
      />
      <p>Select appointment time: </p>
      <div>
        <DateTimePicker onChange={setAppDate} value={appDate} />
      </div>
      <button onClick={setAppointment}>Submit</button>
    </div>
  )
}

export default WorkerPage
