import React, {useState, useEffect} from 'react'
import { useParams } from 'react-router-dom'
import DateTimePicker from 'react-datetime-picker';
import BigCalendar from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

moment.locale('en-GB');
BigCalendar.momentLocalizer(moment);

const allViews = Object
  .keys(BigCalendar.Views)
  .map(k => BigCalendar.Views[k])


const WorkerPage = () => {
  const [value, onChange] = useState(new Date());
  let { worker_id } = useParams();
  let [worker, setWorker]=useState([]);

  useEffect(() => {
    getWorkers()
  }, [])

  let getWorkers = async () => {
    let response = await fetch(`http://127.0.0.1:8000/api/worker/${worker_id}/`)
    let data = await response.json()
    console.log("data: ", data)
    setWorker(data)
  }

  let setAppointment = () => {
    fetch(`http://127.0.0.1:8000/api/add-appointment/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        start_time: value.getHours() + ':' + value.getMinutes(),
        end_time: (value.getHours() + 1) + ':' + value.getMinutes(),
        date: value.getFullYear() + '-' + value.getMonth() + '-' + value.getDate(),
        worker: worker_id
      })
    })
    console.log("Submit appointment")

  }

  console.log("Value: ", value.getFullYear())
  console.log(JSON.stringify({
    start_time: value.getHours() + ':' + value.getMinutes(),
    end_time: (value.getHours() + 1) + ':' + value.getMinutes(),
    date: value.getDate(),
    worker: worker_id
  }))

  return (
    <div>
      <h3>{worker.first_name} {worker.last_name}</h3>
      <small>{worker.specialization}</small>
      <div>{worker.info}</div>
      <div>Worker Calendar:</div>
      <BigCalendar
        //events={events}
        step={60}
        views={allViews}
        defaultDate={new Date(2015, 3, 1)}
      />
      <p>Select appointment time: </p>
      <div>
        <DateTimePicker onChange={onChange} value={value} />
      </div>
      <button onClick={setAppointment}>Submit</button>
    </div>
  )
}

export default WorkerPage
